using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Web;

namespace QuanLy.Utilities
{
    public class EmailTemplate
    {
        //replace {mail-content}

        public static string DefaultTemplate_Base64 = "";
        /// <summary>
        /// Lấy content để gửi mail
        /// Chủ đề: Dự án mới của quý khách đã được khởi tạo!
        /// </summary>
        /// <param name="projectName">Tên dự án</param>
        /// <param name="username">Số điện thoại khách hàng</param>
        /// <returns></returns>
        public static string GetContentNewProject(string projectName, string username)
        {
            var base64EncodedBytes = System.Convert.FromBase64String(DefaultTemplate_Base64);
            string mailContent = System.Text.Encoding.UTF8.GetString(base64EncodedBytes);
            StringBuilder html = new StringBuilder();
            html.Append("<p>Kính gửi quý khách hàng,</p>");
            html.Append("<p><strong>Mona media</strong> xin trân trọng cảm ơn quý khách! Chúc quý khách một ngày mới tốt lành.</p>");
            html.Append("<p>Dự án: <strong>" + projectName + "</strong> của quý khách đã được khởi tạo trên hệ thống quản lý dự án Mona Project Management System của chúng tôi </p>");
            html.Append("<p>Xin truy cập để cập nhật những thông tin mới nhất về dự án qua các cách sau:</p>");
            html.Append("<p>Truy cập <a style=\"text-decoration:none;color:#08c;font-weight: bold;\" href=\"quanly.mona.media\">quanly.mona.media</a></p>");
            html.Append("<p>Download ứng dụng IOS Android</p>");
            html.Append("<p>Tài khoản đăng nhập đã được gửi cho quý khách thông qua SMS vào SĐT: <strong>" + username + "</strong></p>");
            return mailContent.Replace("{mail-content}", html.ToString());
        }
        /// <summary>
        /// gửi khi có phiên thanh toán hoàn thành
        /// </summary>
        /// <param name="cusname"></param>
        /// <param name="dichvu"></param>
        /// <param name="duan"></param>
        /// <param name="sotien"></param>
        /// <param name="datapaid"></param>
        /// <returns></returns>
        public static string GetPaymentSession(string cusname, string lydo, string sotien, string datapaid)
        {
            var base64EncodedBytes = System.Convert.FromBase64String(DefaultTemplate_Base64);
            string mailContent = System.Text.Encoding.UTF8.GetString(base64EncodedBytes);
            StringBuilder html = new StringBuilder();
            html.Append("<p>Kính gửi quý khách " + cusname + ",</p>");
            html.Append("<p><strong>Mona media</strong> đã nhận được thanh toán " + lydo + " của quý khách. Dưới đây là thông tin thanh toán:.</p>");
            html.Append("<p>Thông tin thanh toán:</p>");
            html.Append("<p>" + lydo + " - số tiền: " + sotien + "</p>");
            html.Append("<p>Ngày thanh toán: " + datapaid + "</p>");
            html.Append("<p>Hình thức thanh toán: Chuyển khoản</p>");
            html.Append("<span>Mọi thắc mắc quý khách vui lòng liên hệ bộ phận hỗ trợ hoặc Hotline mona: <a href=\"tel:1900636648\" style =\"text-decoration: none;\">1900 636 648</a> để được hỗ trợ. Xin chân thành cảm ơn quý khách !</span>");
            return mailContent.Replace("{mail-content}", html.ToString());
        }
        /// <summary>
        /// Hết hạn dịch vụ
        /// </summary>
        /// <param name="cusname"></param>
        /// <param name="dichvu"></param>
        /// <param name="duan"></param>
        /// <param name="sotien"></param>
        /// <param name="dateExpires"></param>
        /// <param name="batdau"></param>
        /// <param name="ketthuc"></param>
        /// <returns></returns>
        public static string ServiceExpires(string cusname, string lydo, string sotien, string dateExpires, string batdau, string ketthuc)
        {
            var base64EncodedBytes = System.Convert.FromBase64String(DefaultTemplate_Base64);
            string mailContent = System.Text.Encoding.UTF8.GetString(base64EncodedBytes);
            StringBuilder html = new StringBuilder();
            html.Append("<p>Kính gửi quý khách " + cusname + ",</p>");
            html.Append("<p><strong>Mona media</strong> xin thông báo dịch vụ  " + lydo + " của quý khách sẽ hết hạn vào ngày " + dateExpires + ".</p>");
            html.Append("<p>Thông tin dịch vụ cần gia hạn:</p>");
            html.Append("<p>Dịch vụ: " + lydo + " - số tiền: " + sotien + "</p>");
            html.Append("<p>Ngày bắt đầu: " + batdau + "</p>");
            html.Append("<p>Ngày hết hạn: " + ketthuc + "</p>");
            html.Append("<span>Mọi thắc mắc quý khách vui lòng liên hệ bộ phận hỗ trợ hoặc Hotline mona: <a href=\"tel:1900636648\" style =\"text-decoration: none;\">1900 636 648</a> để được hỗ trợ. Xin chân thành cảm ơn quý khách !</span>");
            return mailContent.Replace("{mail-content}", html.ToString());
        }
    }
}